// Card.js
import React from 'react'
import Button from './Button'

const Card=({title,description,imageUrl,buttonText,onButtonClick})=>{
    return(
        <div className='Card'
style={{
            border: '1px solid #ddd',
            borderRadius:'8px',
            overflow:'hidden',
            boxShadow: '0 4px 8px rgba(0, 0, 1.9, 0,2.5)',
            margin:'20px',
            width:'300px',
            display:'flex',
            flexDirection:'row',


        }}>
        <img
            src={imageUrl}
            alt={title}
            style={{
            width: '100%',
            height: '200px',
            objectFit: 'cover',
            borderRadius: '8px 8px 0 0',
            }}
            />


            <div style={{ padding: '17px' }}>
            <h2 style={{ marginBottom: '10px', fontSize: '25px' }}>{title}</h2>
            <p style={{ marginBottom: '15px' }}>{description}</p>
            <Button onClick={onButtonClick} label={buttonText} />
            </div>
            </div>
            );
            };


            export default Card
