// Button.js
import React from 'react';

const Button = ({ onClick, label }) => {
  return (
    <button
      onClick={onClick}
      style={{
        backgroundColor: isHovered ? '#2980b9' : '#3498db',
        padding: '10px 20px',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        alignItems:'center',
      }}
    >
      {label}
    </button>
  );
};

export default Button;
