// Card.js
import React from 'react';
import Button from './Button';

const Card = ({ title, description, imageUrl, buttonText, onButtonClick }) => {
  return (
    <div
      className="card"
      style={{
        border: '1px solid #ddd',
        borderRadius: '8px',
        overflow: 'hidden',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        width: '300px',
        margin: '20px',
      }}
    >
      <img
        src={imageUrl}
        alt={title}
        style={{
          width: '100%',
          height: '200px',
          objectFit: 'cover',
          borderRadius: '8px 8px 0 0',
        }}
      />
      <div style={{ padding: '' }}>
        <h2 style={{ marginBottom: '8px', fontSize: '1.5rem' }}>{title}</h2>
        <p style={{ marginBottom: '16px' }}>{description}</p>
        <Button onClick={onButtonClick} label={buttonText} />
      </div>
    </div>
  );
};

export default Card;
